"use strict";(self.webpackChunkeiche=self.webpackChunkeiche||[]).push([[8053],{4774:(e,n,i)=>{i.r(n),i.d(n,{assets:()=>d,contentTitle:()=>t,default:()=>u,frontMatter:()=>h,metadata:()=>a,toc:()=>c});var r=i(4848),s=i(8453);const h={sidebar_position:2},t="Kapitel 8: Ein Krematorium f\xfcr Verfolger",a={id:"chapter8",title:"Kapitel 8: Ein Krematorium f\xfcr Verfolger",description:"Nachdem das Auto gestoppt war, griff Bai Yang nach ihrem Handy, bereit auszusteigen.",source:"@site/i18n/de/docusaurus-plugin-content-docs/current/chapter8.md",sourceDirName:".",slug:"/chapter8",permalink:"/Eiche/de/docs/chapter8",draft:!1,unlisted:!1,editUrl:"https://github.com/facebook/docusaurus/tree/main/packages/create-docusaurus/templates/shared/docs/chapter8.md",tags:[],version:"current",sidebarPosition:2,frontMatter:{sidebar_position:2},sidebar:"tutorialSidebar",previous:{title:"Kapitel 7: Eigentlich kein echtes Drama",permalink:"/Eiche/de/docs/chapter7"},next:{title:"Kapitel 9: Eigentlich habe ich mich nicht wirklich gestanden",permalink:"/Eiche/de/docs/chapter9"}},d={},c=[];function l(e){const n={h1:"h1",header:"header",p:"p",...(0,s.R)(),...e.components};return(0,r.jsxs)(r.Fragment,{children:[(0,r.jsx)(n.header,{children:(0,r.jsx)(n.h1,{id:"kapitel-8-ein-krematorium-f\xfcr-verfolger",children:"Kapitel 8: Ein Krematorium f\xfcr Verfolger"})}),"\n",(0,r.jsx)(n.p,{children:"Nachdem das Auto gestoppt war, griff Bai Yang nach ihrem Handy, bereit auszusteigen."}),"\n",(0,r.jsx)(n.p,{children:"Nohm packte ihr Handgelenk und fragte: \u201eSoll ich dich morgen nach der Arbeit hier abholen?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eDas ist nicht n\xf6tig, k\xfcmmere dich erst um deine eigenen Angelegenheiten,\u201c sagte Bai Yang und sch\xfcttelte seine Hand ab, um ihr Gep\xe4ck aus dem Kofferraum zu holen."}),"\n",(0,r.jsx)(n.p,{children:"Der Fahrer half ihr, den Koffer an den Stra\xdfenrand zu stellen: \u201eGute Erholung.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Bai Yang bedankte sich mit einer gefalteten Hand."}),"\n",(0,r.jsx)(n.p,{children:"Nohm stieg nicht aus, als sie durch die Hotelt\xfcr ging."}),"\n",(0,r.jsx)(n.p,{children:"W\xe4hrend der CP-Bindungszeit w\xe4re es am besten, keine Klatsch-Nachrichten zu haben."}),"\n",(0,r.jsx)(n.p,{children:"Als sie schlie\xdflich nach fast Mitternacht in die neben dem Filmset gelegene Wohnung kam, lag Nohm auf dem Bett und drehte sein Handy in den H\xe4nden, gelangweilt und unzufrieden. Er spielte gedankenverloren mit seinem Handy, ohne zu wissen, wohin seine Gedanken drifteten."}),"\n",(0,r.jsx)(n.p,{children:"Das Handy war zur\xfcckgegeben worden, aber sein Herz war verschwunden."}),"\n",(0,r.jsx)(n.p,{children:"Am n\xe4chsten Morgen kaufte Bai Yang ein Flugticket zur\xfcck ins Heimatland. Im Flugzeug durchst\xf6berte sie die Instagram-Nachrichten des letzten Monats und schaute sich ihre Fotos an, wobei sie nur ein paar neue Passbilder fand, alle von ihr selbst gemacht."}),"\n",(0,r.jsx)(n.p,{children:"Schnelles Handeln ist das Geheimnis einer erwachsenen Liebe."}),"\n",(0,r.jsx)(n.p,{children:"Solange noch nicht alles irreparabel ist, nein, besser gesagt, solange es noch nicht angefangen hat, ist es besser, fr\xfchzeitig Schluss zu machen."}),"\n",(0,r.jsx)(n.p,{children:"Nur ein Kuss z\xe4hlt nicht als Anfang."}),"\n",(0,r.jsx)(n.p,{children:"Sie schloss die Augen und Tr\xe4nen liefen unwillk\xfcrlich \xfcber ihre Wangen."}),"\n",(0,r.jsx)(n.p,{children:"Von nun an wird es wohl keine Gelegenheit mehr geben, sich wiederzusehen."}),"\n",(0,r.jsx)(n.p,{children:"Der Regisseur rief immer wieder: \u201eKamera! Kamera! Kamera!\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eNohm, Nohm, jetzt ist die lange Trennung der Hauptdarsteller, sei gl\xfccklich, verstehst du? Sei gl\xfccklich!\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eWie w\xe4re es, wenn wir jetzt erst die Trennung drehen, schlie\xdflich sind wir am Flughafen?\u201c schlug der Regieassistent vor: \u201eNohm ist zu niedergeschlagen, diese Szene wird hundertmal nicht gut.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nach dem Make-up und dem Frisieren ging das Drehen weiter."}),"\n",(0,r.jsx)(n.p,{children:"\u201eWarum weint er mehr als die Schauspielerin?\u201c Der Regisseur fand es etwas unpassend."}),"\n",(0,r.jsx)(n.p,{children:"\u201eSag einfach, ob es gut gespielt ist,\u201c meinte der Assistent."}),"\n",(0,r.jsx)(n.p,{children:"\u201eEs sieht nicht nach gespielt aus.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nohm wurde noch trauriger, als Bai Yang nicht ans Telefon ging, keine Instagram-Nachrichten beantwortete und der Fahrer herausfand, dass sie bereits ausgecheckt hatte."}),"\n",(0,r.jsx)(n.p,{children:"Noch \xe4rgerlicher war, dass er in seinem Geldbeutel 500 Euro mehr fand!"}),"\n",(0,r.jsx)(n.p,{children:"Warum hat sie mir Geld zugesteckt? Was wollte sie bezahlen?"}),"\n",(0,r.jsx)(n.p,{children:"Sie war im Regen zu ihm gelaufen, ihre Stimme wurde vom Regen verschleiert. Jetzt war sie ebenso leise gegangen."}),"\n",(0,r.jsx)(n.p,{children:"Der M\xf6nch sagte: Ehrlichkeit und Best\xe4ndigkeit. Die fehlende Ehrlichkeit war, um sie nicht in unn\xf6tige Kontroversen zu ziehen und die Medien nicht auf sie aufmerksam zu machen. Aber Best\xe4ndigkeit, in welche Richtung soll ich meine Best\xe4ndigkeit ausdr\xfccken?"}),"\n",(0,r.jsx)(n.p,{children:"Nach Tagen voller Verzweiflung, \xc4rger und Streit fand der Regisseur Nohm bei einem Tisch voller Grillfleisch, einer Flasche Wein und einer Portion Ratschl\xe4ge f\xfcr die j\xfcngere Generation:"}),"\n",(0,r.jsx)(n.p,{children:"\u201eAls ich jung war, reiste ich viel, versuchte, gute Werke im Leben zu destillieren. Sp\xe4ter verstand ich, dass die Handlung nicht wichtig ist, sondern das Gef\xfchl die Seele des Werkes ist. Sowohl Schauspieler als auch Regisseure sind Diener der Emotionen. Nohm, als Diener werden deine pers\xf6nlichen Gef\xfchle an bestimmten Stellen das Werk hervorheben und an anderen Stellen die Handlung beeintr\xe4chtigen. Ich m\xf6chte dich nicht in einem unvollst\xe4ndigen Zustand zeigen. Ich gebe dir einen Tag Zeit. Wir k\xf6nnen zuerst andere Szenen drehen. Nach einem Tag kannst du mir einen vollst\xe4ndigen Schauspieler Nohm zur\xfcckgeben?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nohm faltete die H\xe4nde und entschuldigte sich aufrichtig."}),"\n",(0,r.jsx)(n.p,{children:"Auf dem Heimweg durchbl\xe4tterte Nohm mit Erinnerungen und einem L\xe4cheln alte Nachrichten, bis er zu den ersten Nachrichten zur\xfcckkam und pl\xf6tzlich daran dachte, dass er Adressen von Bai Yang erhalten hatte \u2013 eine in Deutschland, die andere in China."}),"\n",(0,r.jsx)(n.p,{children:"Sofort setzte er sich aufrecht hin und \xf6ffnete den Nachrichtenaustausch \u2013 tats\xe4chlich war sie da!"}),"\n",(0,r.jsx)(n.p,{children:"Sein Ziel war klar."}),"\n",(0,r.jsx)(n.p,{children:"Ohne zu z\xf6gern, drehte er das Auto in Richtung Flughafen: \u201eChina, ich komme!\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nachtschwarz und mehrere Zeitzonen trennend, unter Sternen und im Nebel, nach drei Stunden Flug und zwei Stunden Taxifahrt erreichte er endlich Bai Yangs Haust\xfcr."}),"\n",(0,r.jsx)(n.p,{children:"Nohm war fr\xfcher mit Freunden in China gewesen, damals war er noch nicht so bekannt und war in China v\xf6llig anonym. Inzwischen hatten einige Leute seine Werke gesehen, und in den letzten Jahren hatte er an Konzerten und Fan-Treffen teilgenommen, an denen fast eintausend Menschen teilnahmen."}),"\n",(0,r.jsx)(n.p,{children:"Leider, oder gl\xfccklicherweise, erkannte Bai Yang ihn nicht."}),"\n",(0,r.jsx)(n.p,{children:"Er stand vor Bai Yangs Haust\xfcr und wagte es bei dem Licht nicht, zu klopfen. Er trat einen Schritt zur\xfcck und betrachtete die alte, gerade vom Regen nasse Stra\xdfe. Die Efeu rankte sich an den Dachrinnen entlang, ein Zaun trennte die Stra\xdfe von den Wohnh\xe4usern, und durch die niedrigen B\xfcsche konnte man die bl\xfchenden Blumen im Garten erkennen, die an den Bodenlichtern tanzten und bezaubernd aussahen."}),"\n",(0,r.jsx)(n.p,{children:"Bai Yangs Zuhause sah wirklich sch\xf6n aus, und Nohm konnte sich bereits vorstellen, wie sie im sanften Morgenlicht ein wei\xdfes Kleid trug, barfu\xdf \xfcber die Steinplatten im Garten lief und die Bl\xfctenbl\xe4tter der Blumen streichelte, w\xe4hrend sie ihm die T\xfcr \xf6ffnete."}),"\n",(0,r.jsx)(n.p,{children:"W\xfcrde sie sich \xfcber mein Kommen freuen?"}),"\n",(0,r.jsx)(n.p,{children:"Oder w\xfcrde sie mich weiterhin daf\xfcr tadeln, dass ich meine Angelegenheiten nicht geregelt habe?"}),"\n",(0,r.jsx)(n.p,{children:"Er schaute auf seine Uhr: Es war drei Uhr morgens, wahrscheinlich schliefen sie bereits."}),"\n",(0,r.jsx)(n.p,{children:"Also setzte er sich an die T\xfcr, hielt seinen kleinen Rucksack und bewachte die T\xfcr wie die Steinl\xf6wen, die er fr\xfcher gesehen hatte."}),"\n",(0,r.jsx)(n.p,{children:"Der Nachtwind war etwas k\xfchl, aber er war voller Begeisterung und f\xfchlte sich hei\xdf und unruhig. Schlie\xdflich setzte er die Kopfh\xf6rer auf und schloss die Augen, um Musik zu h\xf6ren."}),"\n",(0,r.jsx)(n.p,{children:"Eine Stunde sp\xe4ter klopfte jemand auf seine Schulter."}),"\n",(0,r.jsx)(n.p,{children:"\u201eHey, warum sitzt du vor meiner T\xfcr? Kommst du hierher, um zu \xfcbernachten?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nohm wurde aus seinem halbschlafenden Zustand geweckt, stand auf und w\xe4re fast wegen niedrigem Blutzucker umgekippt. Ein Kribbeln breitete sich von seinen F\xfc\xdfen aus. Gl\xfccklicherweise hielt ihn jemand fest, sonst w\xe4re er gleich auf die Nase gefallen."}),"\n",(0,r.jsx)(n.p,{children:"\u201eHi.\u201c Nohm wollte sich gerne n\xfctzlich machen, nahm schnell die Kopfh\xf6rer ab, holte sein Handy heraus und \xf6ffnete den \xdcbersetzer, um auf Englisch zu sagen: \u201eHallo, ich suche Bai Yang.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Der Mann musterte ihn von oben bis unten: \u201eAusl\xe4nder?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nohm verstand nicht, schwiegen f\xfcr einen Moment und brachte m\xfchsam hervor: \u201eNi hao~\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eHmm, Ausl\xe4nder.\u201c Er zog den Schl\xfcssel heraus und \xf6ffnete die T\xfcr."}),"\n",(0,r.jsx)(n.p,{children:"Nohm folgte ihm hinein, jeder Schritt war eine gro\xdfe Anstrengung: Seine Beine waren noch sehr taub."}),"\n",(0,r.jsx)(n.p,{children:"Der Mann zeigte auf eine Holzbank im Garten und deutete an, dass Nohm sich setzen sollte, und ging dann nach oben, um an die T\xfcr zu klopfen."}),"\n",(0,r.jsx)(n.p,{children:"\u201eKleine Schwester! Da drau\xdfen ist ein Ausl\xe4nder, der dich sucht! Wie hast du es geschafft, einen Wei\xdfen hierher zu bringen?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nohm erschrak so sehr von dieser Stimme, dass er sich sofort aufrichtete."}),"\n",(0,r.jsx)(n.p,{children:"\u201eMach die T\xfcr auf! Bist du tot oder was?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Klopf, klopf, klopf: \u201eMach die T\xfcr auf!\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Die T\xfcr schwang quietschend auf, und Bai Yang hatte noch die Augen geschlossen: \u201eWas ist?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Nohm sprang sofort auf und sp\xe4hte die Treppe hinauf."}),"\n",(0,r.jsx)(n.p,{children:"\u201eSchau dir mal an, wer unten steht.\u201c Er zog Bai Yang auf den Flur, sie lehnte sich tr\xe4ge an das Gel\xe4nder: \u201eWas ist denn?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Sie sah aus wie eine kleine, ver\xe4rgerte Katze."}),"\n",(0,r.jsx)(n.p,{children:"Das Licht wurde von ihrem Bruder eingeschaltet und sie wachte auf."}),"\n",(0,r.jsx)(n.p,{children:"Wann stand da ein kleiner Elf mit hunde\xe4hnlichen Augen im Garten?"}),"\n",(0,r.jsx)(n.p,{children:"\u201eH\xe4?\u201c Bai Yang rieb sich die Augen. Nohm sah sie in ihrem schl\xe4frigen Zustand an und l\xe4chelte erleichtert, winkte ihr vorsichtig zu."}),"\n",(0,r.jsx)(n.p,{children:"\u201eAh!\u201c Sie schrie und rannte die Treppe hinunter, um zu ihm zu eilen."}),"\n",(0,r.jsx)(n.p,{children:"Er lief ein paar Schritte, um sie im Moment ihres Herunterkommens zu fangen, und hielt sie fest in seinen Armen. Bai Yang war wie ein Koala, der an seinem Lieblingsbaum hing."}),"\n",(0,r.jsx)(n.p,{children:"Der Schlaf der jungen Frau roch nach Milch, ihr K\xf6rper war weich wie ein Pudding und schmiegt sich an ihn."}),"\n",(0,r.jsx)(n.p,{children:"Die Tropfen, die von der Dachtraufe fielen, konnten weder die Leidenschaft des jungen Mannes l\xf6schen noch den einzigartigen thail\xe4ndischen Duft von ihm waschen."}),"\n",(0,r.jsx)(n.p,{children:"Nohm klopfte sanft auf ihren R\xfccken und"}),"\n",(0,r.jsx)(n.p,{children:"l\xe4chelte, w\xe4hrend er ihr den Kopf streichelte."}),"\n",(0,r.jsx)(n.p,{children:"Bai Yang legte ihre Arme um seinen Hals und fragte: \u201eWarum bist du hier?\u201c Ihre Stimme war noch von Schlaf durchzogen."}),"\n",(0,r.jsx)(n.p,{children:"Nohm hatte bereits eine Antwort vorbereitet: \u201eWeil du einfach gegangen bist, ohne mir etwas zu sagen, und meine Anrufe und Nachrichten nicht beantwortet hast.\u201c Jetzt, nach langem \xdcberlegen, sagte er nur ein wenig gekr\xe4nkt: \u201eIch habe dich vermisst.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Bai Yang hob den Kopf und sah ihn an, als w\xe4re er ein Hund, der einen Fehler gemacht hat."}),"\n",(0,r.jsx)(n.p,{children:"\u201eWie sehr hast du mich vermisst?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eSo sehr, dass ich nicht arbeiten konnte, so sehr, dass der Regisseur mich entlassen wollte.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eWas?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eDer Regisseur gab mir einen Tag frei.\u201c Endlich erschien ein lang vermisstes L\xe4cheln auf seinem Gesicht."}),"\n",(0,r.jsx)(n.p,{children:"Bai Yang schloss sich ihm an und lie\xdf sich von ihm umarmen."}),"\n",(0,r.jsx)(n.p,{children:"\u201eHabe ich genug gesagt?\u201c fragte ihr Bruder vom Balkon mit einem Glas Wasser. Bai Yang tat so, als w\xfcrde sie es nicht h\xf6ren."}),"\n",(0,r.jsx)(n.p,{children:"Nohm fragte leise: \u201eSoll ich dich nicht mehr umarmen?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eNein, ignorier ihn.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eOkay.\u201c Er streichelte Bai Yang sanft, als ob er ein Kind beruhigte, und ging langsam im Garten umher, sie waren das erste Mal so nah beieinander."}),"\n",(0,r.jsx)(n.p,{children:"Bai Yang wurde erneut schl\xe4frig: \u201eTrag mich bitte nach oben, ich bin so m\xfcde.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eGut.\u201c Er ging ein paar Stufen hinauf und begann leicht zu keuchen. Das schnelle Atmen lie\xdf Bai Yang sofort wach werden. Sie tat so, als sei sie ruhig und sagte: \u201eIch h\xe4tte nicht gedacht, dass du so m\xfcde wirst, obwohl du so gro\xdf aussiehst.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eIch h\xe4tte nicht gedacht, dass du so schwer bist, obwohl du so klein aussiehst.\u201c Er wurde wieder zu dem stolzen kleinen Jungen."}),"\n",(0,r.jsx)(n.p,{children:"\u201eHm? Was hast du gesagt?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eIch sagte, du bist in meinem Herzen sehr wichtig.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eHmph!\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Wenn man wach ist, wei\xdf jeder, dass ein schmalziger Mann nicht in Frage kommt und jemand, der Probleme nicht l\xf6sen kann, ebenfalls nicht."}),"\n",(0,r.jsx)(n.p,{children:"Aber das Leben ist oft verwirrend, und es ist f\xfcr Frauen noch einfacher, verwirrt zu sein."}),"\n",(0,r.jsx)(n.p,{children:"In den B\xfcchern steht: Alle Geschenke des Schicksals sind heimlich mit einem Preis versehen."}),"\n",(0,r.jsx)(n.p,{children:"In diesem Moment, wie in einem romantischen Drama, wird es wahrscheinlich erst nach vielen Schl\xe4gen und Schmerzen klar werden."}),"\n",(0,r.jsx)(n.p,{children:"Aber jetzt ist sie tief darin verstrickt und kann es nicht unterscheiden."}),"\n",(0,r.jsx)(n.p,{children:"Das Zimmer war dunkel. Nohm legte sie sanft auf das Bett und blieb geb\xfcckt stehen, da ihre H\xe4nde noch um seinen Hals lagen."}),"\n",(0,r.jsx)(n.p,{children:"\u201eWillst du nicht ins Bett gehen?\u201c"}),"\n",(0,r.jsx)(n.p,{children:"\u201eIch bin im Flugzeug geflogen, ich bin schmutzig.\u201c"}),"\n",(0,r.jsx)(n.p,{children:"Bai Yang dachte nicht lange nach, zog ihn n\xe4her, ihr kleines Gesicht dr\xfcckte sich an sein Gesicht und sie schmiegte sich weiter an ihn: \u201eJetzt bin ich auch schmutzig.\u201c"})]})}function u(e={}){const{wrapper:n}={...(0,s.R)(),...e.components};return n?(0,r.jsx)(n,{...e,children:(0,r.jsx)(l,{...e})}):l(e)}},8453:(e,n,i)=>{i.d(n,{R:()=>t,x:()=>a});var r=i(6540);const s={},h=r.createContext(s);function t(e){const n=r.useContext(h);return r.useMemo((function(){return"function"==typeof e?e(n):{...n,...e}}),[n,e])}function a(e){let n;return n=e.disableParentContext?"function"==typeof e.components?e.components(s):e.components||s:t(e.components),r.createElement(h.Provider,{value:n},e.children)}}}]);